mapper.select:
select count(*) from emp e

	
		and e.ename like '%'||#{ename}||'%'
	
	
		and e.job like '%'||#{job}||'%'
	
	
		and e.mgr=#{mgr}
	
	
		and e.deptno=#{deptno}

select * from(
select e1.*, rownum rn from(
select e.empno, e.ename,
e.job, e.mgr, e.hiredate, e.sal, e.comm, e.deptno from emp e

	
		and e.ename like '%'||#{ename}||'%'
	
	
		and e.job like '%'||#{job}||'%'
	
	
		and e.mgr=#{mgr}
	
	
		and e.deptno=#{deptno}
	

order by e.hiredate
)
e1)
e2 where e2.rn between #{begin} and #{end}
